import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';

const ColiseumContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #ffe6e6;
`;

const Title = styled.h1`
  color: #cc0000;
  margin-bottom: 20px;
`;

const BattleArea = styled.div`
  display: flex;
  justify-content: space-around;
  width: 80%;
  margin-bottom: 20px;
`;

const CharacterBox = styled.div`
  width: 200px;
  height: 300px;
  border: 2px solid #000;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 10px;
  background-color: #fff;
`;

const CharacterImage = styled.div`
  width: 150px;
  height: 150px;
  background-color: #ccc;
  margin-bottom: 10px;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 12px;
`;

const MeterContainer = styled.div`
  width: 100%;
  height: 20px;
  background-color: #eee;
  margin-bottom: 5px;
`;

const Meter = styled.div`
  height: 100%;
  width: ${props => props.value}%;
  background-color: ${props => props.color};
`;

const CharacterStats = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Coliseum = () => {
  return (
    <ColiseumContainer>
      <Title>Coliseum</Title>
      <BattleArea>
        <CharacterBox>
          <CharacterImage>Player Image</CharacterImage>
          <CharacterStats>
            <MeterContainer>
              <Meter value={100} color="#ff0000" />
            </MeterContainer>
            <div>HP: 100/100</div>
            <MeterContainer>
              <Meter value={100} color="#0000ff" />
            </MeterContainer>
            <div>MP: 10/10</div>
          </CharacterStats>
        </CharacterBox>
        <CharacterBox>
          <CharacterImage>Monster Image</CharacterImage>
          <CharacterStats>
            <MeterContainer>
              <Meter value={100} color="#ff0000" />
            </MeterContainer>
            <div>HP: 100/100</div>
            <MeterContainer>
              <Meter value={100} color="#0000ff" />
            </MeterContainer>
            <div>MP: 10/10</div>
          </CharacterStats>
        </CharacterBox>
      </BattleArea>
      <Link to="/">Return to Main Square</Link>
    </ColiseumContainer>
  );
};

export default Coliseum;
